]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                arr[k-1] &amp;#x3D; temp&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                isSorted &amp;#x3D; false&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                leftSortIndex &amp;#x3D; k&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        leftBorder &amp;#x3D; leftSortIndex&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        console.log(i,arr)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        if (isSorted) break&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    console.timeEnd(&amp;#39;鸡尾酒排序耗时&amp;#39;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    return arr&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;var arr &amp;#x3D; [15, 9, 18, 6, 12, 23, 2, 32, 29]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;console.log(cocktailSort(arr))&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
